import com.company.scrumit.entity.Status;

if (entity.getStatusOld() == Status.Done){
    context["isReady"] = "true";
    return true;
} else if (entity.getStatusOld() == Status.ToDo){
    context["toDo"] = "true";
    return true;
} else if (entity.getStatusOld() == Status.Doing){
    context["inWork"] = "true";
    return true;
} else if (entity.getStatusOld() == Status.Testing){
    context["toCheck"] = "true";
    return true;
} else if (entity.getStatusOld() == Status.Checked){
    context["wasChecked"] = "true";
    return true;
} else if (entity.getStatusOld() == Status.WontFix || entity.getStatusOld() == Status.NotABug || entity.getStatusOld() == Status.Cancelled){
    context["isCancelled"] = "true";
    return true;
} else if(entity.getStatusOld() == null){
    context["toDo"] = "true";
    return true;
}

return false;
