browseScreenGroovyScript:
import com.company.scrumit.entity.*;
import com.haulmont.cuba.gui.components.*;
import com.haulmont.cuba.core.global.*;
import org.apache.commons.collections4.*;
import com.groupstp.workflowstp.entity.Stage;
import com.groupstp.workflowstp.entity.WorkflowInstanceTask;
import org.apache.commons.lang.*;
import com.haulmont.cuba.gui.icons.*;
import com.haulmont.cuba.gui.xml.layout.*;
import com.groupstp.workflowstp.service.WorkflowService;
import com.haulmont.cuba.gui.components.actions.*;
import com.haulmont.chile.core.model.*;
import java.util.*;

stage = stage;
frame = screen;

messages = AppBeans.get(Messages.NAME);
componentsFactory = AppBeans.get(ComponentsFactory.NAME);
service = AppBeans.get(WorkflowService.NAME);

trackerTable = (Table) frame.getComponentNN("trackerTable");

initActions();


private void initActions() {
    ButtonsPanel buttonsPanel = trackerTable.getButtonsPanel();
    initToWorkAction(trackerTable, buttonsPanel, stage, frame, service, messages);
}

private void initToWorkAction(final Table<Tracker> trackerTable, final ButtonsPanel buttonsPanel,
                               final Stage stage, final Frame frame, final WorkflowService service, final Messages messages) {
    Action action = new BaseAction("inWork") {
        @Override
        public String getCaption() {
            return "Взять в работу";
        }

        @Override
        public String getIcon() {
            return CubaIcon.OK.source();
        }

        @Override
        public void actionPerform(Component component) {
            Set<Tracker> selected = trackerTable.getSelected();
            if(!CollectionUtils.isEmpty(selected)){
                commitIfNeed(trackerTable);
                try{
                    for (Tracker problem : selected){
                        try{
                            WorkflowInstanceTask task = service.loadLastProcessingTask(problem,stage);
                            if (task!=null){
                                Map params = new HashMap();
                                params.put("inWork","true");
                                service.finishTask(task,params);
                            }
                        } catch(Exception e){
                            throw new RuntimeException("Ошибка обработки",e);
                        }
                    }
                } finally {
                    trackerTable.getDatasource().refresh();
                }
            }
        }

        @Override
        public boolean isPermitted() {
            if (super.isPermitted()){
                return !CollectionUtils.isEmpty(trackerTable.getSelected());
            }
            return false;
        }
    }    
    Button button = componentsFactory.createComponent(Button.class);
    button.setAction(action);
    buttonsPanel.add(button, 0);
    trackerTable.addAction(action);
}

private static void commitIfNeed(Table<Tracker> trackerTable) {
    if (trackerTable.getDatasource().isModified()) {
        trackerTable.getDatasource().commit();
    }
}

editorScreenGroovyScript:
import com.company.scrumit.entity.*;
import com.haulmont.cuba.gui.components.*;
import com.haulmont.cuba.core.global.*;
import org.apache.commons.collections4.*;
import org.apache.commons.lang.*;
import com.groupstp.workflowstp.entity.Stage;
import com.groupstp.workflowstp.entity.WorkflowInstanceTask;
import org.apache.commons.lang.*;
import com.haulmont.cuba.gui.icons.*;
import com.haulmont.cuba.gui.xml.layout.*;
import com.groupstp.workflowstp.service.WorkflowService;
import com.haulmont.cuba.gui.components.actions.*;
import com.haulmont.chile.core.model.*;
import java.util.*;
import com.haulmont.cuba.gui.data.*;
import com.haulmont.cuba.gui.components.*;

entity = entity;
task = workflowInstanceTask;
editor = screen;
instance = workflowInstance;

messages = AppBeans.get(Messages.NAME);
componentsFactory = AppBeans.get(ComponentsFactory.NAME);
service = AppBeans.get(WorkflowService.NAME);

initActions();

private void initActions() {
    BoxLayout box = (BoxLayout) editor.getComponentNN("bottomPanel");

    box.add(getToWorkButton(entity, task, editor, service, messages),0);

    editor.trackerDs.addItemPropertyChangeListener(new Datasource.ItemPropertyChangeListener<Tracker>() {
        @Override
        public void itemPropertyChanged(Datasource.ItemPropertyChangeEvent<Tracker> e) {
            refreshActionState(box);
        }
    });
    refreshActionState(box);
}

private void refreshActionState(BoxLayout box) {
    for (Component c : box.getComponents()) {
        if (c instanceof Component.ActionOwner) {
            Action action = ((Component.ActionOwner) c).getAction();
            if (action != null) {
                action.refreshState();
            }
        }
    }
}


private Button getToWorkButton(final Tracker problem, final WorkflowInstanceTask task, final AbstractEditor editor, final WorkflowService service, final Messages messages) {
    Action action = new BaseAction("InWork") {
        @Override
        public String getCaption() {
            return "Взять в работу";
        }

        @Override
        public String getIcon() {
            return CubaIcon.OK.source();
        }

        @Override
        public void actionPerform(Component component) {
                try {
                    commit(editor);
                    Map params = new HashMap();
                    params.put("inWork","true");
                    service.finishTask(task, params);
                    editor.close(editor.COMMIT_ACTION_ID, true);
                } catch (Exception e) {
                    throw new RuntimeException("Ошибка обработки заявки", e);
                }

        }    
    };

    Button button = componentsFactory.createComponent(Button.class);
    button.setAction(action);
    return button;
}

private static void commit(AbstractEditor editor) {
    if (editor.isModified()) {
        editor.commitAndClose();
    }
}
